import React, { Component } from "react";
import DatePicker from "react-datepicker";
import moment from "moment";

import "react-datepicker/dist/react-datepicker.css";

class DatePick extends Component {
  constructor() {
    super();
    this.state = {
      startDate: moment(),
      diff:0,
      endDate:moment()

    };
  }
  
  render() {
    return (
       
     <React.Fragment>   
    <DatePicker selected={this.state.startDate}
    onChange={this.handleChangeFirst.bind(this)}
    />
    <DatePicker selected={this.state.endDate}
    onChange={this.handleChangeSecond.bind(this)}
    />
    <button onClick={()=>this.handleDiff()}>calculate</button>
    <label>{this.state.diff}</label>
    </React.Fragment>
      

    );
  }
  handleDiff(){
     const firstDate=this.state.startDate;
     const secondDate=this.state.endDate;
     const diff=firstDate.diff(secondDate);
     const duration=abs(moment.duration(diff).days());
  console.log(duration);
  }
  handleChangeFirst(date) {
    this.setState({
      startDate: date
    });
    console.log(date);
  }
  handleChangeSecond(date) {
    this.setState({
      endDate: date
    });
    console.log(date);
  }
}

export default DatePick;
