import React, { Component } from "react";
import DatePicker from "react-datepicker";
import moment from "moment";
import 'bootstrap/dist/css/bootstrap.css'
import "react-datepicker/dist/react-datepicker.css";

class DatePick extends Component {
  constructor() {
    super();
    this.state = {
      startDate: moment(),
      datesDiff:0,
      endDate:moment()

    };
  }
  
  render() {
    return (
       
     <React.Fragment>   
    <DatePicker selected={this.state.startDate}
    onChange={this.handleChangeFirst.bind(this)}
    />
    <DatePicker selected={this.state.endDate}
    onChange={this.handleChangeSecond.bind(this)}
    />
    <button onClick={()=>this.handleSubstract()}>Subtract Dates</button>
    <span className="badge badge-primary">{this.state.datesDiff} days</span>
    </React.Fragment>
      

    );
  }
  handleSubstract(){
    var now =new moment();
     const startDate=this.state.startDate;
     const endDate=this.state.endDate;
     const datesDiff=Math.abs(startDate.diff(endDate,'days'));
     this.setState({datesDiff:datesDiff})
  console.log(datesDiff);
  }
  handleChangeFirst(date) {
    this.setState({
      startDate: date
    });
    console.log(date);
  }
  handleChangeSecond(date) {
    this.setState({
      endDate: date
    });
    console.log(date);
  }
}

export default DatePick;
